import React, { useRef } from "react";
import Webcam from "react-webcam";

const videoConstraints = {
    width: 540,
    facingMode: "environment"
};
var capture = [null, null, null, null, null, null, null, null];
const Camera = () => {
    const webcamRef = useRef(null);
    const [url, setUrl] = React.useState(null);



    const changePic = React.useCallback(async (idx) => { ///idx라는 매개변수를 사용한 changePic 이름의 함수 생성
      if (idx) {
        var num = 1;
        if (num === 8) return; //num이 8일때 num++ 이전에 강제종료 
        num++;
        const imageTmp = webcamRef.current.getScreenshot();
        capture[idx] = imageTmp;
        setUrl(capture[idx]); //다음 버튼을 누르면 idx값이 1이 되어 num의 값이 1만큼 증가
      }
      var imgTag = document.getElementById("photo");
      imgTag.setAttribute("src", "images/img0" + num + ".image"); //id값이 photo인 이미지태그 선택 후, 
      //src 속성값을 수정
    }, [webcamRef]);

    const onUserMedia = (e) => {
        console.log(e);
    };
    const [number] = React.useState(0);




    return (
        <>
            <Webcam
                ref={webcamRef}
                audio={false} // 오디오 설정 false = mute
                screenshotFormat="image/jpeg" // 저장 확장자 설정 image/jpeg, image/png, image/webp, image/heic
                videoConstraints={videoConstraints}
                onUserMedia={onUserMedia}
                screenshotQuality={1} // 캡쳐화질 설정 0 ~ 1
            // mirrored={false} // 좌우반전 여부 설정 기본값:false
            />
            <h1>{number}</h1>
            <button onClick={changePic(0)}>Capture</button>
            <button onClick={() => setUrl(null)}>Refresh</button>
            {url && (
                <div>
                    <img id="photo" src={capture[0]} alt="Screenshot" />

                </div>
            )}
        </>
    );
};

export default Camera;
