import React, { useRef } from "react";
import Webcam from "react-webcam";

const videoConstraints = {
    width: 540,
    facingMode: "environment"
};
var num = -1;
var capture = [];
const Camera = () => {

    const webcamRef = useRef(null);
    const [url, setUrl] = React.useState(null);
    const capturePhoto_1 = React.useCallback(async () => {


        if (num === 7) return;
        num++;
        const imageTmp = webcamRef.current.getScreenshot();
        capture[num] = imageTmp;
        setUrl(capture[num]);
        setNumber(prevNumber => prevNumber + 1);
        // var image = document.createElement('img');
        // image.innerHTML = capture[num];
        // image.appendChild(capture[num]);

    }, [webcamRef]);

    const onUserMedia = (e) => {
        console.log(e);
    };
    const [number, setNumber] = React.useState(0);




    return (
        <>
            <Webcam
                ref={webcamRef}
                audio={false} // 오디오 설정 false = mute
                screenshotFormat="image/jpeg" // 저장 확장자 설정 image/jpeg, image/png, image/webp, image/heic
                videoConstraints={videoConstraints}
                onUserMedia={onUserMedia}
                screenshotQuality={1} // 캡쳐화질 설정 0 ~ 1
            // mirrored={false} // 좌우반전 여부 설정 기본값:false
            />
            <h1>{number}</h1>
            <button onClick={capturePhoto_1}>Capture</button>
            <button onClick={() => setUrl(null)}>Refresh</button>
            {url && (
                <div id="photo" alt="Screenshot">
                    <div>
                        <img src={capture[0]} alt="Screenshot" />
                    </div>
                    <div>
                        <img src={capture[1]}alt="Screenshot" />
                    </div>
                    <div>
                        <img src={capture[2]}alt="Screenshot" />
                    </div>
                    <div>
                        <img src={capture[3]} alt="Screenshot"/>
                    </div>
                    <div>
                        <img src={capture[4]} alt="Screenshot"/>
                    </div>
                    <div>
                        <img src={capture[5]}alt="Screenshot" />
                    </div>
                    <div>
                        <img src={capture[6]}alt="Screenshot" />
                    </div>
                    <div>
                        <img src={capture[7]} alt="Screenshot"/>
                    </div>


                </div>
            )}
        </>
    );
};

export default Camera;
