import React, { useRef } from "react";
import Webcam from "react-webcam";

const videoConstraints = {
    width: 540,
    facingMode: "environment"
};

var capture = [];
const Camera = () => {
    const webcamRef = useRef(null);
    const [url, setUrl] = React.useState(null);

    const capturePhoto = React.useCallback(async () => {
        const imageSrc_1 = webcamRef.current.getScreenshot();
        capture[0] = imageSrc_1;
        setUrl(capture[0])
        const imageSrc_2 = webcamRef.current.getScreenshot();
        capture[1] = imageSrc_2
        setUrl(capture[1])
        const imageSrc_3 = webcamRef.current.getScreenshot();
        capture[2] = imageSrc_3
        setUrl(capture[2])
        const imageSrc_4 = webcamRef.current.getScreenshot();
        capture[3] = imageSrc_4;
        setUrl(capture[3])
    }, [webcamRef]);

    const onUserMedia = (e) => {
        console.log(e);
    };
    const [number, setNumber] = React.useState(0);

    const onIncrese = () => {
        setNumber(setUrl(
            capture[0], capture[1], capture[2], capture[3]
        ))

    return (
        <>
            <Webcam
                ref={webcamRef}
                audio={false} // 오디오 설정 false = mute
                screenshotFormat="image/jpeg" // 저장 확장자 설정 image/jpeg, image/png, image/webp, image/heic
                videoConstraints={videoConstraints}
                onUserMedia={onUserMedia}
                screenshotQuality={1} // 캡쳐화질 설정 0 ~ 1
            // mirrored={false} // 좌우반전 여부 설정 기본값:false
            />
            <h1>{number}</h1>
            <button onClick={capturePhoto}>Capture</button>
            <button onClick={onIncrese}>+1</button>
            <button onClick={() => setUrl(null)}>Refresh</button>
            {url && (
                <div>
                    <img src={url} alt="Screenshot" />
                </div>
            )}
        </>
    );
};}

export default Camera;
